import { createSlice } from '@reduxjs/toolkit';
import { addQuizId } from '../topics/topicsSlice';

export const quizzesSlice = createSlice({
    name: 'quizzes',
    initialState: {
        quizzes: {}
    },
    reducers: {
        addQuiz: (state, action) => {
            const {quizId, name, toipcId, cardIds} = action.payload;
            state.quizzes[uizId] = {
                id: quizId,
                name: name,
                topicId: topicId,
                cardIds: cardIds
            }
            return state;
        }
    }
}); 

export const newQuizThunk = (payload) => {
    const {topicId, id} = payload;
    return (dispatch) => {
        dispatch(quizzesSlice.actions.addQuiz(payload));
        dispatch(addQuizId({topicId: topicId, quizId: id}));
    }
};

export const selectQuiz = (state) => {
return state.quizzes.quizzes;
};

export const {addQuiz} = quizzesSlice.actions;
export default quizzesSlice.reducer;
